import "./styles/App.css";

import React, { useContext, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import { UserContext } from "./context/userContext";

import Home from "./pages/Home";
import LandingAdmin from "./pages/LandingAdmin/LandingAdmin";
import Income from "./pages/Income";
import DetailProduct from "./pages/DetailProduct";
import Cart from "./pages/Cart";
import Shipment from "./pages/Shipment";
import Profile from "./pages/Profile";
import AddProduct from "./pages/AddProduct";
import NotFound from "./pages/NotFound/NotFound";

// Get API config & setAuthToken
import { API, setAuthToken } from "./config/api";
import PrivateRoute from "./routes/PrivateRoute";

if (localStorage.token) {
  setAuthToken(localStorage.token);
}

function App() {
  const [state, dispatch] = useContext(UserContext);

  const checkUser = async () => {
    try {
      const response = await API.get("/user");

      if (response.status === 404) {
        return dispatch({
          type: "AUTH_ERROR",
        });
      }

      let payload = response.data.data.user;

      payload.token = localStorage.token;

      dispatch({
        type: "USER_SUCCESS",
        payload,
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    checkUser();
  }, []);

  return (
    <Routes>
      <Route exact path="/" element={<Home />} />
      <PrivateRoute path="/landing-admin" element={<LandingAdmin />} />
      <PrivateRoute path="/income" element={<Income />} />
      <PrivateRoute
        path="detailproduct/:productId"
        element={<DetailProduct />}
      />
      <PrivateRoute path="cart" element={<Cart />} />
      <PrivateRoute path="shipment" element={<Shipment />} />
      <PrivateRoute path="profile" element={<Profile />} />
      <PrivateRoute path="addproduct" element={<AddProduct />} />
      <Route path="/error" element={<NotFound />} />
      <Route path="*" element={<Navigate to={"/error"} />} />
    </Routes>
  );
}

export default App;
